var fs = require('fs'),
  path = require('path'),
  Replace;

Replace = function(fileName, staticRoot, fileDest) {
  this.fileName = path.resolve(fileName);
  this.staticRoot = path.resolve(staticRoot);
  this.fileDest = path.resolve(path.dirname(fileDest));
};

Replace.prototype.run = function() {
  var fileName = this.fileName,
    staticRoot = this.staticRoot,
    fileDest = this.fileDest,
    data;

  if (fs.existsSync(fileName)) {
    data = fs.readFileSync(fileName).toString();
    if (data && staticRoot) {
      var cssFilePath = path.resolve(path.dirname(fileName));
      var cssPathDiff = fileDest.substr(
              cssFilePath.indexOf(staticRoot) + staticRoot.length
            ).replace(/\\/g, '/');
      var count = (cssPathDiff.match(/\//g) || []).length;
      
      return data.replace(/url\s*\(\s*(['"]?)([^"'\)]*)\1\s*\)/gi, function(match, location) {
        var dirName = path.resolve(path.dirname(fileName)),
          url,
          urlPath;

        match = match.replace(/\s/g, '');
        url = match.slice(4, -1).replace(/"|'/g, '').replace(/\\/g, '/');
        if (/^\/|https:|http:|data:/i.test(url) === false && dirName.indexOf(staticRoot) > -1) {
          urlPath = path.resolve(dirName + '/' + url);

          if (urlPath.indexOf(staticRoot) > -1) {
            url = urlPath.substr(
              urlPath.indexOf(staticRoot) + staticRoot.length
            ).replace(/\\/g, '/');
          }
          for(i=0;i<count;i++)
            url = '/..' + url;
          
          url = url.substr(1);  

        }

        return 'url("' + url + '")';
      });
    }

    return data;
  }

  return '';
};

module.exports = Replace;
